sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {

    // Get the occasion type from the Radio buttons component
    var occType = event.payload.value;

    // If all is selected, set the occasionTypeQuery value to blank
    if ( occType == 'all'){
        api.setState('occasionTypeQuery', '');
    }

    // Otherwise set occTypeQuery to a value that uses the selected value
    else {
        api.setState('occasionTypeQuery', 'special_occasion=' + occType);
    }
}
